export default function useFetch(c) {
const [data, setData] = useState(null)

useEffect(()=>{
  const ref = collection(db, c)
  const unsub = onSnapshot.doc(ref, (snapshot)=> {
    let results = []
    snapshot.docs.forEach(doc => {
      results.push({id:doc.id,...doc.data()})
      
    });
    setData(results)
  })
},[])

